const objA = { a: 'first' };
const objB = { b: 'second' };
console.log(ObjectUtils.merge(objA, objB));
// Will output { a: 'first', b: 'second' }

const obj = {
  propOne: {
    propOneSub: 'Charito!',
  },
  propTwo: '!!!',
};
console.log(ObjectUtils.get(
  obj,
  'propOne.propOneSub'
